import './App.css';
import {useEffect, useState} from "react";
import logo from "./logo.svg";

function App() {

  const [data,setData]=useState([]);
  const getData=()=>{
    fetch('data.json'
        ,{
          headers : {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
          }
        }
    )
        .then(function(response){
          // console.log(response)
          return response.json();
        })
        .then(function(myJson) {
          console.log(myJson);
          setData(myJson)
        });
  }
  useEffect(()=>{
    getData()
  },[])
  return (
      <div className="App">
          <header className="App-header">
              <div className="row">
                  <div className="column-left">
                      <div>WINE<span className="title-color">SEARCH</span></div>
                  </div>
                  <div className="column-right">

                      <form action="/">
                          <input type="text" placeholder="Recherche" name="search" />
                              <button type="submit">Allons-y</button>
                      </form>

                  </div>
              </div>
          </header>

          <div className="container">

              <div className="row">
                  <div>
                      <button>RÃ©gion</button>
                      <button>Couleur</button>
                      <button>Appellation</button>

                  </div>

              </div>

              <div className="row">
                  <div className="column">
                      <div className="wine-card">


                        name
                      </div>

                      {
                          data && data.length>0 && data.map((item)=><p>Vine name : {item}</p>)
                      }

                  </div>
              </div>

          </div>
      </div>
  );
}

export default App;
